import React, { PureComponent } from 'react';

export default class NameForm extends PureComponent {

    state = {
        name: ""
    }

    handleChange = (e) => {
        this.setState({
            name: e.target.value
        })
    }

    render() {
        return (
            <div>
                <form onSubmit={this.props.saveScore}>
                    <input type="text" placeholder="Name" onChange={this.handleChange} value={this.state.value}></input>
                    <button>Submit Score</button>
                </form>
            </div>
        )
    }
}
